package arrange;

import org.openqa.selenium.WebDriver;
import org.openqa.selenium.chrome.ChromeDriver;
import org.openqa.selenium.edge.EdgeDriver;
import org.openqa.selenium.firefox.FirefoxDriver;
import org.openqa.selenium.ie.InternetExplorerDriver;

import java.util.logging.Logger;

import static org.openqa.selenium.remote.BrowserType.*;

/**
 * Package: arrange
 * Generated by: Hoang.Son.Nguyen
 * Generated at: 01.04.2018 2018
 */
public class OpenCloseSite {


    static WebDriver webDriver;
    private static final Logger myLogger = Logger.getLogger("");

    private static void siteMaximize() {
        webDriver.manage().window().maximize();
    }

    public static void openSiteByUrl(String url) {
        //as default
        webDriver = new ChromeDriver();
        siteMaximize();
        webDriver.get(url);
    }

    public static WebDriver openSiteByUrlBrowser(String url, String browser) {
        switch (browser) {
            case FIREFOX:
                webDriver = new FirefoxDriver();
                break;
            case EDGE:
                webDriver = new EdgeDriver();
                break;
            case IE:
                webDriver = new InternetExplorerDriver();
                break;
            default:
            case CHROME:
                webDriver = new ChromeDriver();
        }

        webDriver.get(url);
        siteMaximize();
        return webDriver;

    }

    public static void closeSite() {
        myLogger.info(webDriver.getCurrentUrl() + " will be closed!");
        webDriver.close();
    }
}
